import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import './Expense.css';
import { useState } from 'react';
const Expense = (props) => {

    const [filteredYear, setFilteredYear] = useState('2020');
    const filterChangeHandler = (selectedYear) => {
        setFilteredYear(selectedYear);
    }
    const filteredExpense = props.items.filter(expense => {
        return expense.date.getFullYear().toString() === filteredYear;
    });
    const filteredContent = <p>No record found</p>;
    if (filteredContent.length > 0) {
        filteredContent =
            filteredExpense.map(expense =>( <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date} />) )

    }


    return (
        <Card className="expense">
            <ExpenseFilter value={filteredYear} onChangeFilter={filterChangeHandler} />

            {/* <ExpenseItem title={props.items[0].title} amount={props.items[0].amount} date={props.items[0].date}></ExpenseItem>
            <ExpenseItem title={props.items[1].title} amount={props.items[1].amount} date={props.items[1].date}></ExpenseItem>
            <ExpenseItem title={props.items[2].title} amount={props.items[2].amount} date={props.items[2].date}></ExpenseItem>
            <ExpenseItem title={props.items[3].title} amount={props.items[3].amount} date={props.items[3].date}></ExpenseItem> */}
        </Card>
    );
}

export default Expense;
